const tableName = 'items'; // items
const primaryKey = 'No'; // No

const dataArray = msg.bc365; // Assuming msg.bc365 contains the array of JSON objects

// Extract all unique column names from all objects in the array
const columnsSet = new Set();
dataArray.forEach(obj => {
    const keys = Object.keys(obj);
    keys.forEach(key => {
        if (key !== primaryKey) {
            columnsSet.add(key);
        }
    });
});

const columns = Array.from(columnsSet); // Convert the Set to an array

var createTableQuery = `CREATE TABLE IF NOT EXISTS \`${tableName}\` (`;
createTableQuery += `\`${primaryKey}\` VARCHAR(255) PRIMARY KEY,`; // Primary key column using "Key" column

// Generate the CREATE TABLE query
for (var i = 0; i < columns.length; i++) {
    createTableQuery += `\`${columns[i]}\` VARCHAR(255)`;

    if (i !== columns.length - 1) {
        createTableQuery += ",";
    }
}

createTableQuery += ") ENGINE=InnoDB";

msg.topic = createTableQuery;

return msg;

