const tableName = 'items';
const existingColumns = msg.payload;
const dataArray = msg.bc365; // Assuming msg.bc365 contains the array of JSON objects

let alterTableQuery = `ALTER TABLE ${tableName} `;
let columnList = '';

// Iterate over each object in the data array
for (let j = 0; j < dataArray.length; j++) {
    const data = dataArray[j];

    // Iterate over the JSON object keys to extract column names
    for (const key in data) {
        if (data.hasOwnProperty(key)) {
            let columnExists = false;
            let columnToAdd = key;

            // Check if the column already exists
            for (let i = 0; i < existingColumns.length; i++) {
                const column = existingColumns[i].COLUMN_NAME;
                if (key === column) {
                    columnExists = true;
                    break;
                }
            }

            // Check if the column is already in the columnList
            if (!columnExists && !columnList.includes(columnToAdd)) {
                columnList += `ADD COLUMN ${columnToAdd} VARCHAR(255), `;
            }
        }
    }
}

// Check if there are missing columns
if (columnList.length > 0) {
    // Remove the trailing comma and space
    columnList = columnList.slice(0, -2);
    alterTableQuery += columnList;

    msg.topic = alterTableQuery;
} else {
    // No missing columns, set alterTableQuery to an empty string
    alterTableQuery = '';
    msg.error = 'No columns to add.';
}

return msg;

