try {
    const exist = msg.payload[0].TOT > 0;
    const tableName = 'items';
    const data = msg.bc365;
    const primaryKey = 'No';
    const escapeString = (value) => {
        if (typeof value === 'string') {
            return "'" + value.replace(/'/g, "''") + "'";
        } else {
            return value;
        }
    };
    let query = '';

    if (exist) {
        query = `UPDATE ${tableName} SET `;

        for (const key in data) {
            if (data.hasOwnProperty(key) && key !== primaryKey) {
                let columnValue = data[key];
                if (typeof columnValue === 'string') {
                    columnValue = columnValue.replace(/'/g, "''");
                    columnValue = `'${columnValue}'`;
                } else if (columnValue instanceof Date) {
                    const formattedDate = columnValue.toISOString().split('T')[0];
                    columnValue = `'${formattedDate}'`;
                }

                query += `\`${key}\` = ${columnValue}, `;
            }
        }

        query = query.slice(0, -2); // Remove the trailing comma and space
        query += ` WHERE \`${primaryKey}\` = ${escapeString(data[primaryKey])}`;
    } else {
        let columns = '';
        let values = '';

        for (const key in data) {
            if (data.hasOwnProperty(key)) {
                let columnValue = data[key];
                if (typeof columnValue === 'string') {
                    columnValue = columnValue.replace(/'/g, "''");
                    columnValue = `'${columnValue}'`;
                } else if (columnValue instanceof Date) {
                    const formattedDate = columnValue.toISOString().split('T')[0];
                    columnValue = `'${formattedDate}'`;
                }

                columns += `\`${key}\`, `;
                values += `${columnValue}, `;
            }
        }

        columns = columns.slice(0, -2); // Remove the trailing comma and space
        values = values.slice(0, -2); // Remove the trailing comma and space

        query = `INSERT INTO ${tableName} (${columns}) VALUES (${values})`;
    }

    msg.topic = query;
} catch (error) {
    msg.payload = error;
}

return msg;

